// 1. Получаем список участников из Zoom
// Важно: убедитесь, что имя узла точно совпадает (Zoom: Get participants data)
const participants = $('Zoom: Get participants data').first().json.participants;

// 2. Получаем текст саммари от AI
// Берем output из входящих данных этого узла (от Create meeting summary1)
const aiOutput = items[0].json.output;

// 3. Формируем тему письма (берем дату и название из данных Zoom)
const meetingTopic = $('Zoom: Get data of last meeting').first().json.topic;
const meetingDate = $('Zoom: Get data of last meeting').first().json.start_time;
const subject = `Meeting Summary: ${meetingTopic} (${meetingDate})`;

// 4. Создаем список писем для отправки
const emailsToSend = [];

for (const person of participants) {
  // Проверяем, есть ли у участника email и не пустой ли он
  if (person.user_email && person.user_email.includes('@')) {
    emailsToSend.push({
      json: {
        to: person.user_email, // Кому: почта конкретного участника
        name: person.name,     // Имя для вежливости (можно использовать в шаблоне)
        subject: subject,      // Тема
        body: aiOutput         // Текст: то самое саммари от AI
      }
    });
  }
}

// 5. Возвращаем массив. n8n запустит следующие узлы для КАЖДОГО элемента массива.
return emailsToSend;

// 1. Получаем текстовый ответ от AI
const aiOutput = $input.item.json.output;

// 2. Превращаем текст в реальный JSON-объект
let parsedData;
try {
  parsedData = JSON.parse(aiOutput);
} catch (error) {
  // Если AI ошибся в синтаксисе, вернем пустой список, чтобы не ломать воркфлоу
  parsedData = { tasks: [] };
}

// 3. Возвращаем массив задач. 
// n8n сам превратит каждый элемент массива в отдельный item для ClickUp
return parsedData.tasks.map(task => ({
  json: task
}));
